import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { TodoService } from './todo.service';
import { TodoItem } from './models';

@Component({
  selector: 'app-root',
  standalone: true,
  imports: [CommonModule, FormsModule],
  template: `
    <div class="app-grid">
      <!-- Header -->
      <header class="app-header">
        <h1>Todo App</h1>
        <p>Organize your tasks efficiently</p>
      </header>

      <!-- Main Content -->
      <main class="app-main">
        <!-- Add Todo Form -->
        <section class="form-section">
          <div class="form-card">
            <h2>Add New Todo</h2>
            <form (ngSubmit)="addTodo()" #todoForm="ngForm" class="todo-form">
              <div class="form-group">
                <input
                  type="text"
                  name="title"
                  [(ngModel)]="newTodoTitle"
                  #titleInput="ngModel"
                  class="form-control"
                  placeholder="Enter a new todo..."
                  required
                  [disabled]="isLoading">
              </div>
              <button
                type="submit"
                class="btn btn-primary"
                [disabled]="!todoForm.valid || isLoading || !newTodoTitle.trim()">
                {{ isLoading ? 'Adding...' : 'Add Todo' }}
              </button>
            </form>
          </div>
        </section>

        <!-- Error Display -->
        <div *ngIf="errorMessage" class="error-card">
          <h3>⚠️ Error</h3>
          <p>{{ errorMessage }}</p>
        </div>

        <!-- Loading State -->
        <div *ngIf="isLoading && todos.length === 0" class="loading-card">
          <div class="spinner"></div>
          <p>Loading todos...</p>
        </div>

        <!-- Todo List -->
        <section *ngIf="!isLoading && todos.length > 0" class="todos-section">
          <div class="todos-header">
            <h2>Your Todos</h2>
            <span class="todo-count">{{ todos.length }} {{ todos.length === 1 ? 'item' : 'items' }}</span>
          </div>
          <div class="todos-grid">
            <div *ngFor="let todo of todos; let i = index" class="todo-card" [style.animation-delay]="i * 0.1 + 's'">
              <div class="todo-content">
                <h3 class="todo-title">{{ todo.title }}</h3>
                <p class="todo-date">{{ formatDate(todo.createdAt) }}</p>
              </div>
              <button
                type="button"
                class="btn btn-danger btn-delete"
                (click)="deleteTodo(todo.id)"
                [disabled]="isLoading"
                title="Delete todo">
                🗑️
              </button>
            </div>
          </div>
        </section>

        <!-- Empty State -->
        <div *ngIf="!isLoading && todos.length === 0 && !errorMessage" class="empty-state">
          <div class="empty-icon">📝</div>
          <h2>No todos yet</h2>
          <p>Add your first todo above to get started!</p>
        </div>
      </main>

      <!-- Footer -->
      <footer class="app-footer">
        <p>Built with Angular 18 & .NET 9</p>
      </footer>
    </div>
  `,
  styleUrls: []
})
export class AppComponent implements OnInit {
  todos: TodoItem[] = [];
  newTodoTitle = '';
  isLoading = false;
  errorMessage = '';

  constructor(private todoService: TodoService) {}

  ngOnInit(): void {
    this.loadTodos();
  }

  loadTodos(): void {
    this.isLoading = true;
    this.errorMessage = '';
    
    this.todoService.getTodos().subscribe({
      next: (todos) => {
        this.todos = todos;
        this.isLoading = false;
      },
      error: (error) => {
        this.errorMessage = error;
        this.isLoading = false;
      }
    });
  }

  addTodo(): void {
    if (!this.newTodoTitle.trim()) {
      return;
    }

    this.isLoading = true;
    this.errorMessage = '';

    this.todoService.addTodo(this.newTodoTitle.trim()).subscribe({
      next: (newTodo) => {
        this.todos = [newTodo, ...this.todos]; // Prepend new todo
        this.newTodoTitle = '';
        this.isLoading = false;
      },
      error: (error) => {
        this.errorMessage = error;
        this.isLoading = false;
      }
    });
  }

  deleteTodo(id: string): void {
    this.isLoading = true;
    this.errorMessage = '';

    this.todoService.deleteTodo(id).subscribe({
      next: () => {
        this.todos = this.todos.filter(todo => todo.id !== id);
        this.isLoading = false;
      },
      error: (error) => {
        this.errorMessage = error;
        this.isLoading = false;
      }
    });
  }

  formatDate(dateString: string): string {
    const date = new Date(dateString);
    return date.toLocaleString();
  }
}
