import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { TodoService } from './todo.service';
import { TodoItem } from './models';

@Component({
  selector: 'app-root',
  standalone: true,
  imports: [CommonModule, FormsModule],
  template: `
    <div class="container">
      <div class="card">
        <h1>Todo App</h1>
        
        <!-- Add Todo Form -->
        <form (ngSubmit)="addTodo()" #todoForm="ngForm">
          <div class="form-group">
            <input
              type="text"
              name="title"
              [(ngModel)]="newTodoTitle"
              #titleInput="ngModel"
              class="form-control"
              placeholder="Enter a new todo..."
              required
              [disabled]="isLoading">
          </div>
          <button
            type="submit"
            class="btn btn-primary"
            [disabled]="!todoForm.valid || isLoading || !newTodoTitle.trim()">
            {{ isLoading ? 'Adding...' : 'Add Todo' }}
          </button>
        </form>

        <!-- Error Display -->
        <div *ngIf="errorMessage" class="error">
          {{ errorMessage }}
        </div>

        <!-- Loading State -->
        <div *ngIf="isLoading && todos.length === 0" class="loading">
          Loading todos...
        </div>

        <!-- Todo List -->
        <div *ngIf="!isLoading && todos.length > 0">
          <h2>Your Todos</h2>
          <div *ngFor="let todo of todos" class="todo-item">
            <div class="todo-title">{{ todo.title }}</div>
            <div class="todo-date">{{ formatDate(todo.createdAt) }}</div>
            <button
              type="button"
              class="btn btn-danger"
              (click)="deleteTodo(todo.id)"
              [disabled]="isLoading">
              Delete
            </button>
          </div>
        </div>

        <!-- Empty State -->
        <div *ngIf="!isLoading && todos.length === 0 && !errorMessage" class="empty-state">
          No todos yet. Add one above to get started!
        </div>
      </div>
    </div>
  `,
  styleUrls: []
})
export class AppComponent implements OnInit {
  todos: TodoItem[] = [];
  newTodoTitle = '';
  isLoading = false;
  errorMessage = '';

  constructor(private todoService: TodoService) {}

  ngOnInit(): void {
    this.loadTodos();
  }

  loadTodos(): void {
    this.isLoading = true;
    this.errorMessage = '';
    
    this.todoService.getTodos().subscribe({
      next: (todos) => {
        this.todos = todos;
        this.isLoading = false;
      },
      error: (error) => {
        this.errorMessage = error;
        this.isLoading = false;
      }
    });
  }

  addTodo(): void {
    if (!this.newTodoTitle.trim()) {
      return;
    }

    this.isLoading = true;
    this.errorMessage = '';

    this.todoService.addTodo(this.newTodoTitle.trim()).subscribe({
      next: (newTodo) => {
        this.todos = [newTodo, ...this.todos]; // Prepend new todo
        this.newTodoTitle = '';
        this.isLoading = false;
      },
      error: (error) => {
        this.errorMessage = error;
        this.isLoading = false;
      }
    });
  }

  deleteTodo(id: string): void {
    this.isLoading = true;
    this.errorMessage = '';

    this.todoService.deleteTodo(id).subscribe({
      next: () => {
        this.todos = this.todos.filter(todo => todo.id !== id);
        this.isLoading = false;
      },
      error: (error) => {
        this.errorMessage = error;
        this.isLoading = false;
      }
    });
  }

  formatDate(dateString: string): string {
    const date = new Date(dateString);
    return date.toLocaleString();
  }
}
